        AND a.created_at BETWEEN #{contractSearchDTO.startAt} AND #{contractSearchDTO.endAt}


        AND a.conr_car_name LIKE CONCAT('%', #{contractSearchDTO.carName}, '%')







                ORDER BY a.conr_cust_name ${sortOrder}


                ORDER BY a.conr_ttl ${sortOrder}


                ORDER BY a.conr_car_name ${sortOrder}


                ORDER BY a.conr_stat ${sortOrder}


                ORDER BY a.conr_comp_name ${sortOrder}


