LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*)
FROM tb_contract a

    a.mem_id = #{memberId}
    AND a.active = TRUE

        AND a.cent_id = #{centerId}


        AND a.conr_name LIKE CONCAT('%', #{title}, '%')


        AND a.conr_cust_name LIKE CONCAT('%', #{customerName}, '%')


        AND a.conr_cust_cla LIKE CONCAT('%', #{customerClassifcation}, '%')


        AND a.conr_stat = #{status}


        AND a.conr_comp_name LIKE CONCAT('%', #{companyName}, '%')


        AND a.conr_cust_pur_cond LIKE CONCAT('%', #{customerPurchaseCondition}, '%')


        AND a.created_at BETWEEN #{startAt} AND #{endAt}


        AND b.prod_name LIKE CONCAT('%', #{productId}, '%')

SELECT
COUNT(*) AS cnt
FROM tb_contract a

    a.active = TRUE

        AND a.mem_id = #{contractSearchDTO.searchMemberId}


        AND a.cent_id = #{contractSearchDTO.centerId}


        AND a.conr_ttl LIKE CONCAT('%', #{contractSearchDTO.title}, '%')


        AND a.conr_cust_name LIKE CONCAT('%', #{contractSearchDTO.customerName}, '%')


        AND a.conr_cust_cla LIKE CONCAT('%', #{contractSearchDTO.customerClassifcation}, '%')


        AND a.conr_stat = #{contractSearchDTO.status}


        AND a.conr_comp_name LIKE CONCAT('%', #{contractSearchDTO.companyName}, '%')


        AND a.conr_cust_pur_cond LIKE CONCAT('%', #{contractSearchDTO.customerPurchaseCondition}, '%')


        AND a.created_at >= #{contractSearchDTO.startAt}


        AND a.created_at <= #{contractSearchDTO.endAt}


        AND a.conr_car_name LIKE CONCAT('%', #{contractSearchDTO.carName}, '%')

