mapper.select:
SELECT SUM(a.sal_hist_ince) as INCENTIVE, SUM(a.sal_hist_no_of_veh) as PERFORMANCE, SUM(a.sal_hist_tota_sale) as TOTAL_SALES FROM tb_sales_history a
a.mem_id = #{salesHistorySearchDTO.searcherName} AND a.active = TRUE
AND a.created_at BETWEEN #{salesHistorySearchDTO.startDate} AND #{salesHistorySearchDTO.endDate}

SELECT a.mem_id, SUM(a.sal_hist_ince) AS total_incentive, SUM(a.sal_hist_no_of_veh) AS total_performance, SUM(a.sal_hist_tota_sale) AS total_sales FROM tb_sales_history a
a.mem_id IN
#{mem_id}
AND a.created_at BETWEEN #{salesHistoryRankedDataDTO.startDate} AND #{salesHistoryRankedDataDTO.endDate}
AND a.active = TRUE
GROUP BY a.mem_id LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) AS rank_count FROM( SELECT COUNT(*) FROM tb_sales_history a
a.mem_id IN
#{mem_id}
AND a.created_at BETWEEN #{salesHistoryRankedDataDTO.startDate} AND #{salesHistoryRankedDataDTO.endDate}
AND a.active = TRUE
GROUP BY a.mem_id) AS A

SELECT a.mem_id, SUM(a.sal_hist_ince) AS total_incentive, SUM(a.sal_hist_no_of_veh) AS total_performance, SUM(a.sal_hist_tota_sale) AS total_sales, LEFT(a.created_at, 7) AS MONTH FROM tb_sales_history a
a.mem_id IN
#{mem_id}
AND a.created_at BETWEEN #{salesHistoryRankedDataDTO.startDate} AND #{salesHistoryRankedDataDTO.endDate}
AND a.active = TRUE
GROUP BY a.mem_id, LEFT(a.created_at, 7) ORDER BY MONTH ASC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) AS rank_count FROM( SELECT COUNT(*) FROM tb_sales_history a
a.mem_id IN
#{mem_id}
AND a.created_at BETWEEN #{salesHistoryRankedDataDTO.startDate} AND #{salesHistoryRankedDataDTO.endDate}
AND a.active = TRUE
GROUP BY a.mem_id, LEFT(a.created_at, 7)) AS A

SELECT LEFT(a.created_at, 4) AS YEAR, a.mem_id, SUM(a.sal_hist_ince) AS total_incentive, SUM(a.sal_hist_no_of_veh) AS total_performance, SUM(a.sal_hist_tota_sale) AS total_sales FROM tb_sales_history a
a.mem_id IN
#{mem_id}
AND a.created_at BETWEEN #{salesHistoryRankedDataDTO.startDate} AND #{salesHistoryRankedDataDTO.endDate}
AND a.active = TRUE
GROUP BY a.mem_id, LEFT(a.created_at, 4) ORDER BY YEAR ASC LIMIT #{size} OFFSET #{offset}

