#{mem_id}
AND a.created_at BETWEEN #{salesHistoryRankedDataDTO.startDate} AND #{salesHistoryRankedDataDTO.endDate}
AND a.active = TRUE
GROUP BY a.mem_id, LEFT(a.created_at, 4)) AS A

